import { connection } from 'mongoose';
import connectDB from '..';
import {
  User, Theme, Exercise, Favorite,
} from '../models';

// DÃ©finir ExerciseType
const ExerciseType = {
  TEXT: 'TEXT',
  TABLE: 'TABLE',
  VIDEO: 'VIDEO',
};

// DÃ©finir TextContent
class TextContent {
  constructor(content) {
    this.content = content;
  }
}

const seed = async () => {
  console.log('Nettoyage de la base de donnÃ©es');

  await connectDB();
  await connection.dropDatabase();

  console.log('Base de donnÃ©es nettoyÃ©e');

  // CrÃ©ation des thÃ¨mes
  const themes = [
    new Theme({
      title: 'Gestion du stress',
      description: 'Exercices pour gÃ©rer le stress au quotidien',
      shortDescription: 'Exercices de gestion du stress',
      backgroundImage: 'plop',
    }),
    new Theme({
      title: 'Affirmations positives',
      description: 'Pratique d\'affirmations pour encourager la pensÃ©e positive',
