      backgroundImage: 'plop',
    }),
    new Theme({
      title: 'Mindfulness',
      description: 'Pratique de la pleine conscience et de la mÃ©ditation',
      shortDescription: 'Pleine conscience et mÃ©ditation',
      backgroundImage: 'plop',
    }),
  ];

  // CrÃ©ation des exercices
  const exercises = [
    new Exercise({
      title: 'Respiration consciente',
      description: 'Exercice de respiration pour se relaxer',
      shortDescription: 'Exercice de respiration',
      theme: themes[0]._id,
      type: ExerciseType.TEXT,
      content: new TextContent('Plop'),
      backgroundImage: 'url_de_l_image',
    }),
    new Exercise({
      title: 'Visualisation de relaxation',
      description: 'Exercice de visualisation pour se dÃ©tendre',
      shortDescription: 'Exercice de visualisation',
      theme: themes[0]._id,
      type: ExerciseType.TEXT,
      content: new TextContent('Plop'),
      backgroundImage: 'url_de_l_image',
    }),
    new Exercise({
      title: 'Affirmations positives',
      description: 'Exercice pour dÃ©velopper les affirmations positives',
      shortDescription: 'DÃ©velopper des affirmations positives',
      theme: themes[1]._id,
      type: ExerciseType.TEXT,
      content: new TextContent('Plop'),
      backgroundImage: 'url_de_l_image',
    }),
    new Exercise({
      title: 'Analyse de problÃ¨me',
      description: 'Exercice d\'analyse pour rÃ©soudre des problÃ¨mes',
      shortDescription: 'Analyser les problÃ¨mes',
      theme: themes[2]._id,
      type: ExerciseType.TEXT,
      content: new TextContent('Plop'),
      backgroundImage: 'url_de_l_image',
    }),
    new Exercise({
      title: 'MÃ©ditation de pleine conscience',
      description: 'Exercice de mÃ©ditation pour dÃ©velopper la pleine conscience',
      shortDescription: 'DÃ©velopper la pleine conscience',
      theme: themes[3]._id,
      type: ExerciseType.TEXT,
      content: new TextContent('Plop'),
      backgroundImage: 'url_de_l_image',
    }),
    new Exercise({
      title: 'Exercice vidÃ©o 1',
      description: 'Exercice vidÃ©o pour la pratique de TCC',
      shortDescription: 'Exercice vidÃ©o TCC',
      theme: themes[0]._id,
      type: ExerciseType.VIDEO,
      content: new TextContent('Plop'),
      backgroundImage: 'url_de_l_image',
    }),
    new Exercise({
      title: 'Exercice tableau 1',
      description: 'Exercice de tableau pour amÃ©liorer la concentration',
      shortDescription: 'AmÃ©liorer la concentration',
      theme: themes[1]._id,
      type: ExerciseType.TABLE,
      content: new TextContent('Plop'),
      backgroundImage: 'url_de_l_image',
    }),
  ];

  // CrÃ©ation de trois utilisateurs
  const users = [
    new User({ name: 'Utilisateur 1', email: 'user1@email.com', password: 'password' }),
    new User({ name: 'Utilisateur 2', email: 'user2@email.com', password: 'password' }),
    new User({ name: 'Utilisateur 3', email: 'user3@email.com', password: 'password' }),
  ];

  // CrÃ©ation des favoris pour chaque utilisateur
  const favorites = [
    new Favorite({ user: users[1]._id, exercise: exercises[0]._id }),
    new Favorite({ user: users[1]._id, exercise: exercises[1]._id }),
